    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    <script>
        // Get transaction data from PHP
        var transactions = {!! json_encode($transactions) !!};

        // Process data to count canceled, success, and pending transactions
        var canceled = transactions.filter(transaction => transaction.status === 'canceled').length;
        var success = transactions.filter(transaction => transaction.status === 'success').length;
        var pending = transactions.filter(transaction => transaction.status === 'pending').length;

        // Pie chart
        var pieCtx = document.getElementById('pieChart').getContext('2d');
        var pieChart = new Chart(pieCtx, {
            type: 'pie',
            data: {
                labels: ['Canceled', 'Success', 'Pending'],
                datasets: [{
                    data: [canceled, success, pending],
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.5)',
                        'rgba(54, 162, 235, 0.5)',
                        'rgba(255, 206, 86, 0.5)'
                    ]
                }]
            },
            options: {
                // Add options here if needed
            }
        });

        // Bar chart
        var barCtx = document.getElementById('barChart').getContext('2d');
        var barChart = new Chart(barCtx, {
            type: 'bar',
            data: {
                labels: ['Canceled', 'Success', 'Pending'],
                datasets: [{
                    label: 'Transactions',
                    data: [canceled, success, pending],
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.5)',
                        'rgba(54, 162, 235, 0.5)',
                        'rgba(255, 206, 86, 0.5)'
                    ],
                    borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)'
                    ],
                    borderWidth: 1
                }]
            },
            options: {
                // Add options here if needed
            }
        });
    </script>
